    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        if (tblPhieuNhap.getSelectedRowCount()!= 1) {
            System.out.println("Chưa chọn phiếu nhập");
            
            return;
            
        }
        String idPN = tblPhieuNhap.getValueAt(tblPhieuNhap.getSelectedRow(), 1).toString();
        SP.BLL.BLL_CTPhieuNhap.InsertSanPhamTrongKho_By_PhieuNhap(idPN);
    }//GEN-LAST:event_jButton2ActionPerformed

