    private void tblPhieuNhapMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblPhieuNhapMouseClicked
        if (evt.getClickCount() == 2) {
            page_Main_NhapHang.pnlAddPnl.removeAll();
            jdlNhapHang.pageCTPhieuXuatNhap = new page_CT_PhieuNhap_Xuat();
            page_Main_NhapHang.pnlAddPnl.add(jdlNhapHang.pageCTPhieuXuatNhap).setVisible(true);
            page_Main_NhapHang.pnlAddPnl.revalidate();
            page_Main_NhapHang.pnlAddPnl.repaint();
        }
    }//GEN-LAST:event_tblPhieuNhapMouseClicked

